import { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

function Clasificacion() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    async function fetchUsers() {
      // Consulta para obtener la media de Param1, Param2 y Param3 para cada usuario
      const { data, error } = await supabase
        .from('users')
        .select('id, username, votos(param1, param2, param3)')
        .eq('votos.convocatoria_id', 'id'); // Filtramos votos por convocatoria (user id)

      if (error) {
        console.log('Error:', error);
      } else {
        // Procesamos los datos para calcular la puntuación
        const usersWithScores = data.map(user => {
          const votos = user.votos || [];
          const totalVotos = votos.length;

          // Si no tiene votos, asignamos 0 como puntuación
          if (totalVotos === 0) {
            return { ...user, puntuacion: 0 };
          }

          // Calcular la media de Param1, Param2 y Param3
          const totalParam1 = votos.reduce((acc, voto) => acc + voto.param1, 0);
          const totalParam2 = votos.reduce((acc, voto) => acc + voto.param2, 0);
          const totalParam3 = votos.reduce((acc, voto) => acc + voto.param3, 0);

          const puntuacionMedia = (totalParam1 + totalParam2 + totalParam3) / (totalVotos * 3);

          return { ...user, puntuacion: puntuacionMedia };
        });

        // Ordenar los usuarios por puntuación descendente
        const sortedUsers = usersWithScores.sort((a, b) => b.puntuacion - a.puntuacion);

        setUsers(sortedUsers);
      }
    }

    fetchUsers();
  }, []);

  return (
    <div>
      <h2>Clasificación</h2>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Puntuación</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.username}</td>
              <td>{user.puntuacion.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Clasificacion;
