import { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom'; // Importamos el hook para redirigir
import './Login.css'; // Importamos el CSS para aplicar los estilos

function Login({ setUser }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate(); // Hook para la navegación

  const handleLogin = async (e) => {
    e.preventDefault(); // Prevenir la recarga de página

    if (!username || !password) {
      setErrorMessage('Username y contraseña son obligatorios');
      return;
    }

    // Consultamos la base de datos para encontrar al usuario con el username y password correctos
    const { data, error } = await supabase
      .from('users')
      .select('*')
      .eq('username', username)
      .eq('password', password)
      .single();  // Para que devuelva un solo usuario si coincide

    if (error) {
      setErrorMessage('Error al iniciar sesión: ' + error.message);
    } else if (data) {
      console.log('Inicio de sesión exitoso:', data);
      setUser(data);  // Guardamos el usuario en el estado global y localStorage
      setErrorMessage('');  // Limpiamos cualquier mensaje de error

      // Redirigimos a la página de votación después de un inicio de sesión exitoso
      navigate('/vota');
    } else {
      setErrorMessage('Credenciales incorrectas');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type="submit">Login</button>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

export default Login;
