import { Link } from 'react-router-dom';
import { useUser } from '../UserContext'; // Importamos el hook para usar el contexto del usuario
import './Navbar.css'; // Estilos del Navbar

function Navbar() {
  const { user, setUser } = useUser(); // Obtenemos el estado del usuario desde el contexto

  const handleLogout = async () => {
    await supabase.auth.signOut(); // Cerramos sesión en Supabase
    localStorage.removeItem('user'); // Eliminamos el usuario de localStorage
    setUser(null); // Actualizamos el estado del usuario en el contexto
  };

  return (
    <nav>
      <ul>
        <li>
          <Link to="/proximamente">PRÓXIMAMENTE...</Link>
        </li>
        <li>
          <Link to="/clasificacion">CLASIFICACIÓN</Link>
        </li>

        {user ? (
          <>
            <li>
              <Link to="/vota">VOTA</Link>
            </li>
            <li>
              <button onClick={handleLogout}>LOGOUT</button>
            </li>
          </>
        ) : (
          <li>
            <Link to="/">LOGIN</Link>
          </li>
        )}
      </ul>
    </nav>
  );
}

export default Navbar;
