import { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

function Clasificacion() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    async function fetchData() {
      // Paso 1: Agrupar votos por 'convocatoria' y sumar los Param1, Param2 y Param3
      const { data: votosData, error: votosError } = await supabase
        .from('voto')
        .select('convocatoria, SUM(param1) as total_param1, SUM(param2) as total_param2, SUM(param3) as total_param3')
        .group('convocatoria');

      if (votosError) {
        console.log('Error obteniendo los votos:', votosError);
        return;
      }

      // Paso 2: Relacionar los resultados con la tabla 'users' para obtener el 'username'
      const { data: usersData, error: usersError } = await supabase
        .from('users')
        .select('id, username');

      if (usersError) {
        console.log('Error obteniendo los usuarios:', usersError);
        return;
      }

      // Paso 3: Combinar los datos de votos con los usernames
      const combinedData = votosData.map(voto => {
        const user = usersData.find(user => user.id === voto.convocatoria); // Buscamos el username que coincida con el id de la convocatoria
        return {
          username: user ? user.username : 'Usuario desconocido',
          total: voto.total_param1 + voto.total_param2 + voto.total_param3
        };
      });

      setUsers(combinedData); // Guardamos los resultados en el estado
    }

    fetchData();
  }, []);

  return (
    <div>
      <h2>Clasificación</h2>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>Total Puntuación</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.username}</td>
              <td>{user.total}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Clasificacion;
