import { useState } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import { useUser } from '../UserContext'; // Importamos el hook para actualizar el estado del usuario
import './Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();
  const { setUser } = useUser(); // Obtenemos el método para actualizar el usuario

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setErrorMessage('Username y contraseña son obligatorios');
      return;
    }

    const { data, error } = await supabase
      .from('users')
      .select('*')
      .eq('username', username)
      .eq('password', password)
      .single();

    if (error) {
      setErrorMessage('Error al iniciar sesión: ' + error.message);
    } else if (data) {
      console.log('Inicio de sesión exitoso:', data);
      setUser(data);  // Actualizamos el estado del usuario globalmente
      setErrorMessage('');  // Limpiamos cualquier mensaje de error
      navigate('/vota');  // Redirigimos a /vota
    } else {
      setErrorMessage('Credenciales incorrectas');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
}

export default Login;
