import { useState } from 'react';
import { supabase } from '../supabaseClient';

function Login({ setUser }) {  // Pasamos setUser como prop para almacenar el usuario
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    const { data, error } = await supabase
      .from('users')
      .select('*')
      .eq('username', username)
      .eq('password', password)
      .single();

    if (error) {
      console.log('Error al iniciar sesi贸n:', error.message);
    } else if (data) {
      console.log('Inicio de sesi贸n exitoso:', data);
      setUser(data);  // Guardamos los datos del usuario en el estado global/local
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
        />
        <input 
          type="password" 
          placeholder="Password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
