import { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';

function Proximamente() {
  const [convocatorias, setConvocatorias] = useState([]);
  const today = new Date(); // Fecha actual

  useEffect(() => {
    async function fetchConvocatorias() {
      // Llamada a la función SQL para obtener username y fecha
      const { data: convocatoriasData, error } = await supabase.rpc('get_proximas_convocatorias');

      if (error) {
        console.log('Error obteniendo convocatorias:', error);
        return;
      }

      setConvocatorias(convocatoriasData); // Guardamos los resultados en el estado
    }

    fetchConvocatorias();
  }, []);

  const formatDate = (dateString) => {
    let date = new Date(dateString);

    // Si la fecha es inválida, intenta forzar el formato ISO (YYYY-MM-DD a YYYY-MM-DDTHH:MM:SS)
    if (isNaN(date.getTime())) {
      // Forzamos el formato añadiendo una hora a la fecha si viene solo con el formato YYYY-MM-DD
      date = new Date(`${dateString}T00:00:00`);
    }

    return date;
  };

  return (
    <div>
      <h2>Próximas Convocatorias</h2>
      <ul>
        {convocatorias.map((convocatoria, index) => {
          // Convertimos la fecha usando la función formatDate
          const convocatoriaDate = formatDate(convocatoria.fecha);

          // Si la fecha sigue siendo inválida, mostramos un mensaje de error
          if (isNaN(convocatoriaDate.getTime())) {
            return <li key={index}><p>Fecha inválida para {convocatoria.username}</p></li>;
          }

          return (
            <li key={index}>
              {convocatoriaDate >= today ? (
                // Si la fecha es mayor o igual a hoy, mostramos el mensaje con la fecha
                <p>{convocatoria.username} cocinará el {convocatoriaDate.toLocaleDateString()}</p>
              ) : (
                // Si la fecha es menor a hoy, mostramos el mensaje alternativo
                <p>{convocatoria.username} no tiene fecha, pero que se vaya preparando</p>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Proximamente;
